query:
select wo.`name`, wo.custom_item_full_name, wo.qty, substring(woi.item_code, 6) as flat_sz, wo.planned_start_date, if(fbi.item_code is not null, substring(fbi.item_code, 6), null) as potup_flat_sz
from ((`tabWork Order` wo inner join `tabWork Order Item` woi on (wo.`name` = woi.parent) and (woi.parenttype = 'Work Order') and (woi.item_code like 'FLAT-%'))
    left join `tabBOM` fb on (wo.custom_followup_bom = fb.`name`))
    left join `tabBOM Item` fbi on (fb.name = fbi.parent) and (fbi.parenttype = 'BOM') and (fbi.item_code like 'FLAT-%');
